import React, { useState } from 'react';
import './Header.css';

import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Button from '@material-ui/core/Button';
import Modal from 'react-modal';
import Paper from '@material-ui/core/Paper';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';

import logo from '../../assets/logo.svg';

Modal.setAppElement('#root');

export default function Header(props){
  const [modalStatus, setModalStatus] = useState(false);

  const toggleModal = () => {
        setModalStatus(!modalStatus);
  }

  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return(
      <React.Fragment>
        {/* NavBar Code*/}
        <AppBar position="static" id='app-bar-style'>
                <Toolbar>
                    <img src={logo} className='brand-icon-style rotate-image'/>
                    <Button variant="contained" color="primary" className='book-show-btn-style'>Book Show</Button>
                    <Button variant="contained" color="default" className='login-logout-btn-style' onClick={toggleModal}>
                        Login
                    </Button>
                </Toolbar>
        </AppBar>
        
        {/* Modal Code */}
        <Modal
            style={{width: "50%", height: "50%"}}
            isOpen={modalStatus}
            onRequestClose={toggleModal}
            contentLabel="My dialog">
                <div>
                <Paper>
                    <Tabs
                        value={value}
                        onChange={handleChange}
                        indicatorColor="primary"
                        textColor="primary"
                        centered>
                        <Tab label="LOGIN" />
                        <Tab label="REGISTER" />
                    </Tabs>
                </Paper>
                </div>
                <Button variant="contained" color="primary"  onClick={toggleModal}>Close modal</Button>
        </Modal>
        </React.Fragment>

  );
}
