import React, { useState } from 'react';
import './Header.css';

import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Button from '@material-ui/core/Button';
import Modal from 'react-modal';

import logo from '../../assets/logo.svg';

Modal.setAppElement('#root');

export default function Header(props){
  const [modalStatus, setModalStatus] = useState(false);

  const toggleModal = () => {
        setModalStatus(!modalStatus);
  }

  const padding = 90; // adjust this to your needs
  let height = '20px';
  let heightPx = height + 'px';
  let heightOffset = height / 2;
  let offsetPx = heightOffset + 'px';

  const style = {
    content: {
      border: '4',
      borderRadius: '4px',
      bottom: 'auto',
      height: heightPx,  // set height
      left: '50%',
      padding: '2rem',
      position: 'fixed',
      right: 'auto',
      top: '50%', // start from center
      transform: 'translate(-50%,-' + offsetPx + ')', // adjust top "up" based on height
      width: '40%',
      maxWidth: '40rem'
    }
  };

  return(
      <React.Fragment>
        <AppBar position="static" id='app-bar-style'>
                <Toolbar>
                    <img src={logo} className='brand-icon-style rotate-image'/>
                    <Button variant="contained" color="primary" className='book-show-btn-style'>Book Show</Button>
                    <Button variant="contained" color="default" className='login-logout-btn-style' onClick={toggleModal}>
                        Login
                    </Button>
                </Toolbar>
        </AppBar>
        <Modal
            style={style} 
            isOpen={modalStatus}
            onRequestClose={toggleModal}
            contentLabel="My dialog">
                <div>My modal dialog.</div>
                <button onClick={toggleModal}>Close modal</button>
        </Modal>
        </React.Fragment>

  );
}
